INSERT INTO publishers_news VALUES('38','60448','post','ombeni-sefue-angolewa-ikulu','http://globalpublishers.co.tz/ombeni-sefue-angolewa-ikulu/','publish','Ombeni Sefue ang&#8217;olewa Ikulu','Ombeni Sefue ang&#8217;olewa Ikulu','<p><img class=\"aligncenter size-full wp-image-40563\" src=\"http://globalpublishers.co.tz/wp-content/uploads/2015/11/sefue.jpg\" alt=\"sefue\" width=\"595\" height=\"300\" srcset=\"http://globalpublishers.co.tz/wp-content/uploads/2015/11/sefue-300x151.jpg 300w, http://globalpublishers.co.tz/wp-content/uploads/2015/11/sefue.jpg 595w\" sizes=\"(max-width: 595px) 100vw, 595px\" /></p>\n<p style=\"text-align: center;\"><strong>Aliyekuwa Katibu Mkuu Kiongozi, Ombeni Sefue .</strong></p>\n<p><img class=\"aligncenter size-large wp-image-60449\" src=\"http://globalpublishers.co.tz/wp-content/uploads/2016/03/Kijazi-1024x731.jpg\" alt=\"Kijazi\" width=\"620\" height=\"443\" srcset=\"http://globalpublishers.co.tz/wp-content/uploads/2016/03/Kijazi-300x214.jpg 300w, http://globalpublishers.co.tz/wp-content/uploads/2016/03/Kijazi-768x549.jpg 768w, http://globalpublishers.co.tz/wp-content/uploads/2016/03/Kijazi-1024x731.jpg 1024w\" sizes=\"(max-width: 620px) 100vw, 620px\" /></p>\n<p style=\"text-align: center;\">Aliyeteuliwa kuwa Katibu Mkuu Kiongozi, Mhandisi John William Kijazi.</p>\n<p style=\"text-align: left;\">Rais John Pombe Magufuli, amemteua Mhandisi John William Kijazi kuwa Katibu Mkuu Kiongozi, kuchukua nafasi ya Ombeni Sefue ambaye atapangiwa kazi nyingine. Kabla ya uteuzi huo, Mhandisi Kijazi alikuwa Balozi wa Tanzania nchini India.</p>\n<p style=\"text-align: left;\"><a href=\"https://2.bp.blogspot.com/-_vtr7taSemE/Vtw2z6ME64I/AAAAAAAAbqA/8Uq2ndgrwrU/s1600/Cc3geuJUcAAOZBS.jpg%2Blarge.jpg\"><img class=\"aligncenter\" src=\"https://2.bp.blogspot.com/-_vtr7taSemE/Vtw2z6ME64I/AAAAAAAAbqA/8Uq2ndgrwrU/s640/Cc3geuJUcAAOZBS.jpg%2Blarge.jpg\" alt=\"\" width=\"563\" height=\"720\" border=\"0\" /></a></p>\n','<p>Aliyekuwa Katibu Mkuu Kiongozi, Omben