INSERT INTO transsion_info VALUES('26','','','','<p align=\"left\">\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';\"><br />\n</span>\n</p>\n<p align=\"left\">\n	<span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:14px;\">传音控股旗下品牌质量有保证，受人欢迎。当然，这是好事——但是也有其缺点。</span> \n</p>\n<p align=\"left\">\n	<span style=\"font-family:\'Microsoft YaHei\';\"></span>\n</p>\n<p align=\"left\">\n	<span style=\"font-family:宋体;\"><span style=\"line-height:2;font-family:Microsoft YaHei;color:#666666;font-size:14px;\"></span></span><span style=\"color:#000000;\">&nbsp;</span> \n</p>\n<p align=\"left\">\n	<span style=\"font-family:宋体;\"><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:14px;\">近年来，假冒伪劣的</span><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:14px;\">TECNO</span><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:14px;\">手机、</span><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:14px;\">TECNO</span><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:14px;\">配件的非法生产和销售急剧上升。快速交易迅速暴露了它的低劣质量，也成为了一个安全隐患；同时造成了巨大的经济损失。</span><span></span></span> \n</p>\n<p align=\"left\">\n	<span style=\"font-family:\'Microsoft YaHei\';\"></span>\n</p>\n<p align=\"left\">\n	<span style=\"font-family:宋体;\"><span style=\"line-height:2;font-family:Microsoft YaHei;color:#666666;font-size:14px;\"></span></span><span style=\"color:#000000;\">&nbsp;</span> \n</p>\n<p align=\"left\">\n	<span style=\"font-family:宋体;\"><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:14px;\">保护你自己以防买到假冒伪劣产品的最好方法就是到</span><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:14px;\">TECNO、</span><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:14px;\">itel和Infinix</span><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:14px;\">授权经销商处或者是</span><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:14px;\">Carlcare</span><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:14px;\">服务中心购买产品、备件和配件或者是向他们征求意见。</span><span></span></span> \n</p>\n<p align=\"left\">\n	<span style=\"font-family:宋体;\"><span style=\"font-family:Microsoft YaHei;\"><span style=\"line-height:2;font-family:Microsoft YaHei;white-space:normal;color:#666666;font-size:14px;\"></span></span></span><span style=\"color:#000000;\">&nbsp;</span> \n</p>\n<p align=\"left\">\n	<p align=\"left\">\n		<span style=\"font-family:宋体;color:#000000;\"><span style=\"font-family:Microsoft YaHei;color:#000000;\"><span style=\"line-height:2;font-family:Microsoft YaHei;white-space:normal;color:#000000;font-size:14px;\">产品盗版盛行于许多领域——不仅仅是软件、手表、服装设计、药物遭到毒手，手机行业（从配件到整机）也无法幸免。传音致力于将消费者可能经受的风险和危险减到最少。</span> \n	</p>\n</span></span> \n	<p>\n		<br />\n	</p>\n	<p align=\"left\">\n		<span style=\"font-family:\'Microsoft YaHei\';font-size:9pt;\"></span>\n	</p>\n	<p align=\"left\">\n		<p style=\"white-space:normal;\" align=\"left\">\n			<span style=\"line-height:2;font-family:Microsoft YaHei;color:#666666;font-size:9pt;\"><span style=\"font-family:Microsoft YaHei;color:#666666;font-size:14px;\"></span></span>&nbsp;\n		</p>\n		<p style=\"white-space:normal;\" align=\"left\">\n			<span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:9pt;\"><span style=\"font-family:Microsoft YaHei;color:#000000;font-size:14px;\">伪造产品的非法生产和销售还在不断增加，包括：</span></span> \n		</p>\n		<p style=\"white-space:normal;\" align=\"left\">\n			<span style=\"line-height:2;font-family:Microsoft YaHei;color:#666666;font-size:9pt;\"><span style=\"font-family:Microsoft YaHei;color:#666666;font-size:14px;\"></span></span><span style=\"color:#000000;\">&nbsp;</span> \n		</p>\n		<blockquote style=\"border-bottom:medium none;border-left:medium none;padding-bottom:0px;margin:0px 0px 0px 40px;padding-left:0px;padding-right:0px;white-space:normal;border-top:medium none;border-right:medium none;padding-top:0px;\">\n			<p align=\"left\">\n				<span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:14px;\">- TECNO</span><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:14px;\"><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;\">原装正版手机</span></span> \n			</p>\n			<p align=\"left\">\n				<span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:14px;\">- TECNO</span><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:14px;\"><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;\">原装正版配件</span></span> \n			</p>\n		</blockquote>\n		<p style=\"white-space:normal;\" align=\"left\">\n			<span style=\"font-family:\'Microsoft YaHei\';font-size:9pt;\"></span>\n		</p>\n		<p>\n			<span style=\"font-family:宋体;white-space:normal;font-size:9pt;\"><span style=\"line-height:2;font-family:\'Microsoft YaHei\';\"><span style=\"font-size:14px;\"></span><span style=\"font-family:Microsoft YaHei;color:#666666;font-size:14px;\"></span></span></span><span style=\"color:#000000;\">&nbsp;</span> \n		</p>\n		<p>\n			<span style=\"font-family:宋体;white-space:normal;font-size:9pt;\"><span style=\"line-height:2;font-family:\'Microsoft YaHei\';color:#000000;\"><span style=\"font-family:Microsoft YaHei;color:#000000;font-size:14px;\">那些乍一看是价格低廉的产品经常被证明是质量低劣仿造品，然而</span></span><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:14px;\">,</span><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;font-size:14px;\">对消费者造成的由假冒产品造成的损失通常大于预期的节约。</span></span> \n		</p>\n		<p>\n			<br />\n		</p>','2','?tag=26','/uploads/13993688866354.jpg','','/uploads/13995189459523.jpg','<p align=\"left\" style=\"white-space:normal;\">\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:9pt;line-height:2;\"><br />\n</span> \n</p>\n<p align=\"left\" style=\"white-space:normal;\">\n	<br />\n</p>')
INSERT INTO transsion_info VALUES('27','Infinix','Infinix','','<p>\n	<span style=\"line-height:1.5;font-family:Microsoft YaHei;font-size:14px;\"><strong></strong></span>&nbsp;\n</p>\n<p>\n	<span style=\"line-height:1.5;font-family:Microsoft YaHei;font-size:14px;\"><strong>Infinix Mobility</strong>（传音控股旗下品牌之一），自2009年创立，已经成为全球智能手机和平板电脑市场的主要竞争品牌。其产品通过“时尚+科技”的设计理念，结合电商模式，创新功能以及完美品质，赢得数百万消费者的青睐。</span> \n</p>\n<p>\n	<span style=\"line-height:1.5;font-family:Microsoft YaHei;font-size:14px;\"></span>&nbsp;\n</p>\n<p>\n	<span style=\"line-height:1.5;font-family:Microsoft YaHei;font-size:14px;\">秉承“挑战常规”的品牌精神，Infinix智能设备专门为这样一群年轻人而设计，他们希望脱颖而出，希望与世界同步、走在时尚的前沿。其提供的高品质智能产品为这类人群打造了一个与众不同的智能生活方式。</span> \n</p>\n<p>\n	&nbsp;\n</p>\n<p>\n	<span style=\"line-height:1.5;font-family:Microsoft YaHei;font-size:14px;\">Infinix抛开陈规，立足于无线和移动互联网领域的科技前沿，直通未来之门。</span> \n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<div>\n	<br />\n</div>','2','?tag=27','/images/infinix2.jpg','','/images/banner_infinix2.jpg','')
INSERT INTO transsion_info VALUES('28','iFLUX','iFLUX','iFLUX','<p>\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"><br />\n</span>\n</p>\n<p>\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"></span><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">iFLUX，是TRANSSION HOLDINGS旗下的专业LED照明品牌。</span><br />\n<br />\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">iFLUX用全球化思维结合本地化洞察，将先进的LED照明技术转化成优秀的本土化的照明产品。同时，iFLUX加大用户体验的投入比重，采用最前沿的创新技术，尽最大可能为消费者提供最合适他们的LED照明产品。</span><br />\n<br />\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">iFLUX有着包括室内照明，室外照明，太阳能照明，手持照明设备，可充电系统等等在内的极其全面的产品线。iFLUX产品的核心理念是——品质照明，品质生活。依托强大的研究开发和生产制造能力，iFLUX为消费者提供质量可靠，价格合理，感官舒适的照明产品。</span><br />\n<br />\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">iFLUX实施从研发到生产的全流程质量检控，拥有高效的物流分销体系，提供依托于Carlcare的专业售后服务，在产品周期的各个环节，iFLUX都能做到行业领先。</span><br />\n<br />\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\">毫无疑问，iFLUX将会是非洲地区发展最迅速的照明品牌，也将成为最受非洲消费者喜爱的照明品牌。</span>\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>','2','?tag=28','/uploads/14440272142780.jpg','','/uploads/14440272105545.jpg','')
INSERT INTO transsion_info VALUES('31','Operating System','Operating System','Operating System','<p>\n	<br />\n</p>\n<p>\n	<a></a><span style=\"line-height:2;font-family:\'Microsoft YaHei\';color:#666666;font-size:32px;\">卓越，源自标准</span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"></span>&nbsp;\n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"><span style=\"font-family:\'Microsoft YaHei\';color:#666666;\"><span style=\"font-size:16px;\"><strong><span style=\"line-height:2;color:#000000;\">· </span></strong></span></span>传音控股经过多年的发展，已经形成了相对成熟的运营体系，包括人力资源管理体系,财务管理体系,营销管理体系,生产与质量管理体系,供应链管理体系等。</span> \n</p>\n<p>\n	<a></a><a></a><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"><span style=\"font-family:\'Microsoft YaHei\';color:#666666;\"><span style=\"font-size:16px;\"><strong><span style=\"line-height:2;color:#000000;\">· </span></strong></span></span>经过多年的探索与总结，传音业务已形成了以芯片支持、主板研发（采用自主研发与第三方研发相结合的方式）、供应链管理（元器件和配件）、</span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">新产品开发、生产制造、品牌与市场动作、渠道拓展、产品销售与售后服务于一体的，贯穿产业链各个环节的一站式业务体系。</span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"><br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"><br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"><br />\n</span>\n</p>\n<p>\n	<br />\n</p>','3','?tag=31','','','/uploads/14005815227233.png','<p>\n	<span style=\"line-height:2;font-size:14px;font-family:\'Microsoft YaHei\';\"> </span> \n</p>\n<p>\n	<br />\n</p>')
INSERT INTO transsion_info VALUES('32','研究开发','研究开发','研究开发','<p>\n	<span style=\"font-size:12px;\"></span>\n</p>\n<p>\n	<span style=\"line-height:1.5;font-family:Microsoft YaHei;font-size:12px;\">&nbsp;&nbsp; </span>\n</p>\n<p>\n	<span style=\"line-height:1.5;font-family:Microsoft YaHei;font-size:14px;\">自主研发作为战略发展的一个重要方向，传音坚持以市场为驱动，用户为导向的研发模式进行自主创新，提供适合用户需求的产品。</span> \n</p>\n<p>\n	<span style=\"line-height:1.5;font-family:Microsoft YaHei;font-size:12px;\">&nbsp;&nbsp; </span>\n</p>\n<p>\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\"><span style=\"line-height:1.5;\">公司拥有自己的研发中心，分别在法国巴黎、中国上海和中国深圳设立了设计研发机构，每年将最新的潮流设计融入手机设计，使我们的产品始终处在潮流前沿</span><span style=\"line-height:1.5;font-size:12px;\">。</span></span> \n</p>\n<p>\n	<span style=\"line-height:1.5;font-family:Microsoft YaHei;font-size:12px;\">&nbsp;&nbsp; </span>\n</p>\n<p>\n	<span style=\"line-height:1.5;font-family:Microsoft YaHei;font-size:14px;\">同时，凝聚了一大批素质过硬的全球顶级手机厂商的软、硬件技术骨干，并积累了丰富的项目开发经验。独立自主的开发主体，层次分明、科学规范的创新体系、持续的研发投入，确保传音产品具备世界一流水准。如今，传音产品因其可靠的品质和时尚的设计而倍受用户青睐。</span> \n</p>','3','?tag=32','/uploads/13994555417367.jpg','','/uploads/13994555313666.jpg','<p>\n	<span style=\"font-family:\'Microsoft YaHei\';text-indent:24px;white-space:normal;font-size:14px;\"><br />\n</span> \n</p>\n<p>\n	<br />\n</p>')
INSERT INTO transsion_info VALUES('33','供应链管理','供应链管理','供应链管理','<p>\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\">&nbsp;&nbsp;</span>\n</p>\n<p>\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\">供应链上的每一个环节，对产品成败都起到关键作用。自成立以来，传音将供应链视为自身重要的组成部分。传音不仅对零部件的引入有着严格的质量控制标准，而且对供应商的质量也进行严格把控，最终形成了一套富于传音特色的高效的评分系统和审核体系。</span>\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\">传音一直坚持与行业中一流的供应商保持高效、紧密的合作，共同成长。供应链上的不同企业通过建设跨平台的信息系统实现信息共享，保障供应体系内信息的准确、及时传递，保证业务流程的顺畅进行。这些措施的采用，不仅有效地控制了成本，还能快速地响应终端市场的需求。正是通过和供应链伙伴的共同努力，造就了传音产品经久不衰的传奇。</span>\n</p>','3','?tag=33','/uploads/13993737813262.jpg','','/uploads/13993737369863.jpg','<p>\n	<span style=\"font-family:\'Microsoft YaHei\';white-space:normal;\"><span style=\"font-size:14px;line-height:2;\"><br />\n</span></span> \n</p>\n<p>\n	<br />\n</p>')
INSERT INTO transsion_info VALUES('34','','','','<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';color:#666666;font-size:24px;\">您怎样才能成为我们的供应商？</span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">传音科技始终把质量摆在首位，这正是我们为什么期望供应商成为行业翘楚，能够与国际竞争对手平分秋色，并追求持续发展的原因。&nbsp;我们的供应商通过提供创新产品和可靠服务、按时交货和市场定价等方式给予我们大力支持。&nbsp;作为回报，我们与他们建立了平等互利的长期合作伙伴关系。</span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">您想申请成为传音的供应商吗？&nbsp;如果您满足我们的供应商基本要求，可把贵公司相关情况发到此邮箱 <u><span style=\"color:#003399;\"><a href=\"mailto:purchase@transsion.com\">purchase@transsion.com</a></span></u>。</span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"><strong></strong></span>&nbsp;\n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"><strong>一、对于产品及品牌的基本要求</strong></span> \n</p>\n<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">1.&nbsp;有良好的品牌声誉，在行业内有一定的知名度；</span><br />\n<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">2.&nbsp;产品质量优良，生产商有严格的产品质量控制体系；</span><br />\n<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">3.&nbsp;生产厂家有完善的售中和售后服务；</span><br />\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">4.&nbsp;产品需求满足各种强制认证、规定及要求（我司要求的认证有CE、ROSH等）。</span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"><br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"> \n	<p style=\"white-space:normal;\">\n		<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"><strong>二、对于供应商的基本要求</strong></span> \n	</p>\n<span style=\"line-height:2;font-family:\'Microsoft YaHei\';white-space:normal;font-size:14px;\">1.&nbsp;提供有竞争力的价格及政策，能够保证足够的产能和稳定的货源；</span><br style=\"white-space:normal;\" />\n<span style=\"line-height:2;font-family:\'Microsoft YaHei\';white-space:normal;font-size:14px;\">2.&nbsp;接受账期付款或银行承兑；</span><br style=\"white-space:normal;\" />\n<span style=\"line-height:2;font-family:\'Microsoft YaHei\';white-space:normal;font-size:14px;\">3.&nbsp;代理商需能提供所代理产品的有效品牌授权文件，厂家需能提供所生产产品的商标注册文件；</span><br style=\"white-space:normal;\" />\n<span style=\"line-height:2;font-family:\'Microsoft YaHei\';white-space:normal;font-size:14px;\">4.&nbsp;能提供有效的公司证件（营业执照、税务登记证、组织机构代码证）；</span><br style=\"white-space:normal;\" />\n<span style=\"line-height:2;font-family:\'Microsoft YaHei\';white-space:normal;font-size:14px;\">5.&nbsp;具备一般纳税人资格，对于所有供应的产品，均能提供增值税专用发票，且发票内容与实际采购货物一致；</span><br style=\"white-space:normal;\" />\n	<p style=\"white-space:normal;\">\n		<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">6.&nbsp;提供良好的售后服务支持。</span> \n	</p>\n<br />\n</span> \n	<p>\n		<br />\n	</p>','3','?tag=34','/uploads/13993547581922Apply-to-Be-a-Supplier_s.jpg','','/uploads/13993547536114Apply-to-Be-a-Supplier.jpg','<p>\n	<span style=\"font-family:Microsoft YaHei;\"><span style=\"color:#666666;font-family:\'Microsoft YaHei\';font-size:23.636363983154297px;line-height:2;white-space:normal;\"> </span><span style=\"font-family:\'Microsoft YaHei\';line-height:2;\"></span><span style=\"font-family:\'Microsoft YaHei\';line-height:2;\"> </span></span> \n</p>\n<p>\n	<br />\n</p>')
INSERT INTO transsion_info VALUES('35','生产制造','生产制造','生产制造','<div>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';color:#666666;font-size:24px;\">生产基地</span> \n</div>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">依托中国大陆这片工业基础设施完备、产业配套体系完善的沃土，经过多年的发展</span><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">与3次扩张，传音控股拥有厂房面积近20000平方米，员工2000多人，生产线40多条，</span><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">是深圳这个中国最大的手机生产基地内具有高度自动化、现代化的手机专业生产基地</span><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">之一。所有工厂均采用“7S”系统来创造安全、整洁、有序的生产环境，以确保产品的</span><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">生产程序高效运转。</span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">为了满足持续发展的需求，传音现正筹划进一步扩大工厂面积，把其打造成中国最大</span><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">最好的手机专业生产基地之一。</span> \n</p>\n<div>\n	<br />\n<br />\n<span style=\"line-height:2;font-family:\'Microsoft YaHei\';white-space:normal;color:#666666;font-size:24px;\">产能</span><br style=\"white-space:normal;\" />\n<span style=\"line-height:2;font-family:\'Microsoft YaHei\';white-space:normal;font-size:14px;\">传音这专业的生产基地和顶级生产设备为其提供了强大的生产制造能力，日产手机近15万部，年产量3000万部以上，为各个品牌提供不间断的产品支持，为消费者带去高质量的产品。</span><br />\n</div>','3','?tag=35','/uploads/13993738352108.jpg','','/uploads/13993738304813.jpg','<br />')
INSERT INTO transsion_info VALUES('36','质量体系','质量体系','质量体系','<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';color:#666666;font-size:24px;\">“质量永存”</span><br />\n<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">传音将产品质量视为生命，“质量永存”是传音对产品质量管控的深刻理解。</span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">传音拥有一套完善的质量管理体系。在产品质量控制的整个过程中，有超过200名质检</span><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">人员进行全面的检测，其检测项目多达168项。传音强大的质量保证体系和富有经验的</span><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">质量管理团队，为传音的手机产品提供全方位的品质保障，并通过每一段的严格检测来</span><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">确保提供质量可靠的，具备世界一流标准的产品交到合作伙伴和最终用户的手上。</span> \n</p>\n<p>\n	<br />\n</p>\n<p style=\"white-space:normal;\">\n	<span style=\"line-height:2;\"><span style=\"font-family:\'Microsoft YaHei\';color:#666666;font-size:24px;\">质量证书</span></span> \n</p>\n<p style=\"white-space:normal;\">\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">TECNO的全部产品都通过了ISO9000资质认证，目前正在积极申请ISO14000和</span><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">ISO18000标准。此外，公司已获得海关A类企业资格和商检二类企业资格，并正在作</span><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">进一步升级。在供应商质量控制上，传音控股与华勤、龙旗、锐嘉科、萨基通、MKT</span><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">等一流的方案公司通力合作，全方位确保公司能够将具备世界一流标准的产品交到合</span><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">作伙伴和最终用户的手中。</span> \n</p>\n<p style=\"white-space:normal;\">\n	<br />\n</p>\n<p style=\"white-space:normal;\">\n	<img style=\"white-space:normal;\" alt=\"\" src=\"/uploads/1399366738.jpg\" /><img alt=\"\" src=\"/uploads/1399366747.jpg\" /> \n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>','3','?tag=36','/uploads/13993739052145.jpg','','/uploads/13993738458671.jpg','<p>\n	<br />\n</p>')
INSERT INTO transsion_info VALUES('37','市场营销','市场营销','市场营销','<p style=\"text-align:justify;\" align=\"left\">\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';color:#666666;font-size:24px;\">渠道建设</span> \n</p>\n<p style=\"text-align:justify;\" align=\"left\">\n	<span style=\"font-family:宋体;font-size:9pt;\"><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">庞大而运转灵活的销售网络，是企业的核心竞争力，对企业起着至关重要的作用。</span><span></span></span> \n</p>\n<p style=\"text-align:justify;\" align=\"left\">\n	<span style=\"font-family:宋体;font-size:9pt;\"><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">传音采取以国家级代理渠道为主线，以省级代理渠道为基础的策略，扎根非洲市场。通过多年的努力，在包括尼日利亚、肯尼亚、坦桑尼亚、乌干达、加纳、埃塞俄比亚、马里、喀麦隆、刚果金等在内的</span><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">20</span><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">多个非洲国家建立起了销售网络。</span><span></span></span> \n</p>\n<p style=\"text-align:justify;\" align=\"left\">\n	<span style=\"font-family:宋体;font-size:9pt;\"><br />\n</span>\n</p>\n<p align=\"left\">\n	<p style=\"text-align:justify;white-space:normal;\" align=\"left\">\n		<span style=\"line-height:2;font-family:宋体;font-size:9pt;\"><span style=\"line-height:2;font-family:\'Microsoft YaHei\';color:#666666;font-size:24px;\">品牌塑造</span></span> \n	</p>\n	<p style=\"text-align:justify;white-space:normal;\" align=\"left\">\n		<span style=\"font-family:宋体;font-size:9pt;\"><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">传音控股自成立以来就十分注重品牌的建设，努力提升企业核心竞争力。传音旗下手机终端品牌</span><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">TECNO、</span><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">itel和Infinix</span><span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">凭借独特的营销手法和品牌战略在非洲手机市场异军突起，成为非洲手机的中坚力量。</span></span> \n	</p>\n	<p>\n		<br />\n	</p>','3','?tag=37','/uploads/13993744035861.jpg','','/uploads/13993740028109.jpg','<p align=\"left\" style=\"text-align:justify;white-space:normal;\">\n	<br />\n</p>')
INSERT INTO transsion_info VALUES('38','客户服务','客户服务','客户服务','<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';color:#666666;font-size:32px;\">您的满意是我们最大的回报</span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">质量优秀的售后服务是良好销售量的保障。产品售出后，传音仍然将其视为企业经营活动的一部分，力求确保每一个传音客户都得到良好的服务。</span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"><span style=\"line-height:28px;font-family:\'Microsoft YaHei\';white-space:normal;font-size:14px;\"></span></span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"><span style=\"line-height:28px;font-family:\'Microsoft YaHei\';white-space:normal;font-size:14px;\"><span style=\"line-height:1.5;\"></span>我们在非洲拥有售后网点最多，服务最好的售后网络。目前，我们拥有<strong>80</strong>多</span><span style=\"line-height:28px;font-family:\'Microsoft YaHei\';white-space:normal;font-size:14px;\">个Carlcare售后服务中心，且有更多的售后中心正在建设中。我们拥有超过<strong>8</strong></span><strong style=\"line-height:28px;font-family:\'Microsoft YaHei\';white-space:normal;font-size:14px;\">00</strong>名<span style=\"line-height:28px;font-family:\'Microsoft YaHei\';white-space:normal;font-size:14px;\">专业的售后服务人员，为我们的客户提供最好的服务。</span> </span>\n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"><span style=\"line-height:28px;font-family:\'Microsoft YaHei\';white-space:normal;font-size:14px;\"><br />\n</span></span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"><span style=\"line-height:28px;font-family:\'Microsoft YaHei\';white-space:normal;font-size:14px;\"><br />\n</span></span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"><span style=\"line-height:28px;font-family:\'Microsoft YaHei\';white-space:normal;font-size:14px;\"><br />\n</span></span> \n</p>','3','?tag=38','/uploads/13993744534703.jpg','','/uploads/13993744178240.jpg','<p>\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:32px;line-height:2;\"> </span> \n</p>\n<p>\n	<br />\n</p>')
INSERT INTO transsion_info VALUES('41','职业生涯','职业生涯','职业生涯','<p>\n	<br />\n</p>\n<p>\n	<span style=\"line-height:2;font-family:Microsoft YaHei;color:#666666;font-size:32px;\">设计你的职业，开创你的生活</span> \n</p>\n<p>\n	<SPAN style=\"FONT-FAMILY: \" color:#333333;?=\"\"></span>\n</p>\n<p>\n	<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';color:#666666;font-size:16px;\"><br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';color:#000000;font-size:14px;\"><span style=\"font-family:Microsoft YaHei;color:#666666;\"><span style=\"font-family:Microsoft YaHei;font-size:16px;\"><strong><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;\">· </span></strong></span></span><span style=\"font-family:Microsoft YaHei;\">在传音，一切充满激情。在这里，你可以把一份工作转为你的事业；在这里，我们将挑战变为机遇，将创新的想法融入行动。</span></span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';color:#000000;font-size:14px;\"><span style=\"font-family:Microsoft YaHei;color:#666666;\"><span style=\"font-family:Microsoft YaHei;font-size:16px;\"><strong><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;\">· </span></strong></span></span><span style=\"font-family:Microsoft YaHei;\">我们对移动通讯的热情使我们的团队成</span></span><span style=\"line-height:2;font-family:Microsoft YaHei;font-size:14px;\">为一个强大的队伍，在这个团队里我们的每一意见都会受到重视。</span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"><span style=\"font-family:Microsoft YaHei;color:#666666;\"><span style=\"font-family:Microsoft YaHei;font-size:16px;\"><strong><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;\">· </span></strong></span></span><span style=\"font-family:Microsoft YaHei;\">正是因为当技术专家、高端专业流程和工作的乐趣融合在一起，我们才能一起塑造更好的未来。</span></span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"><span style=\"font-family:Microsoft YaHei;color:#666666;\"><span style=\"font-family:Microsoft YaHei;font-size:16px;\"><strong><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;\">· </span></strong></span></span><span style=\"font-family:Microsoft YaHei;\">无论你期待的是什么，在传音，你都可以和我们分享你的激情，找到你的未来。</span></span> \n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>','4','?tag=41','','','/uploads/13993464512744careers.jpg','')
INSERT INTO transsion_info VALUES('42','共创、共享','共创、共享','共创、共享','<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';color:#666666;font-size:32px;\">共创、共享</span> \n</p>\n<p>\n	<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';font-size:14px;\">传音一直致力于以透明、高效的管理和不断地自我创新来使所有股东，合作伙伴和传音大家庭所有成员满意。凭着“共创，共享”的核心价值观，传音和众多合作伙伴相融共生，并取得极大的成功。</span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"><span style=\"line-height:1.5;font-size:12px;\">&nbsp; </span><br />\n</span>\n</p>\n<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"><span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';white-space:normal;font-size:14px;\">面对激烈的市场竞争，传音始终坚持同样的精神，相信未来仍能始终与合作伙伴保持一个共赢的局面。</span><br />\n</span>\n</p>','1','?tag=42','/uploads/13996287964815.jpg','','/uploads/13996287918223.jpg','<p>\n	<span style=\"font-size:32px;line-height:2;font-family:\'Microsoft YaHei\';\"> </span> \n</p>\n<p>\n	<br />\n</p>')
INSERT INTO transsion_info VALUES('43','可持续健康发展','可持续健康发展','可持续健康发展','<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';color:#666666;font-size:32px;\">可持续性健康发展</span> \n</p>\n<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\">传音在追求自我生存和永续发展的过程中秉承着“可持续健康发展”的经营理念，既考虑公司当前经营目标的实现和提升公司品质，又极力保持公司在未来扩张的经营环境中始终保持持续的盈利增长和能力的提高，保证公司在相当长的时间内长胜不衰。</span>','1','?tag=43','/uploads/13993722102823.jpg','','/uploads/13994415091785.jpg','')
