INSERT INTO transsion_info VALUES('48','薪资福利','薪资福利','薪资福利','<p align=\"left\">\n	<b style=\"line-height:1.5;font-size:12px;\"><span style=\"line-height:2;font-family:宋体;font-size:24px;\"><span style=\"line-height:2;font-family:\'Microsoft YaHei\';color:#666666;font-size:24px;\"></span></span></b> \n</p>\n<h3>\n	<span style=\"line-height:173%;font-family:\'Microsoft YaHei\';color:#666666;font-size:24px;\">休闲活动</span> \n</h3>\n<p align=\"left\">\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#666666;\">传音控股为员工们提供不同的休闲活动，包括瑜伽班、足球赛、篮球赛、羽毛球赛等等。</span> \n</p>\n<p align=\"left\">\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#666666;\">还有各种小吃福利，如水果餐、自动贩卖机补贴等等。</span><span style=\"font-size:9.0pt;\"></span> \n</p>\n<h3>\n	<span style=\"font-size:9pt;line-height:173%;font-family:宋体;color:#666666;\"><span style=\"font-family:\'Microsoft YaHei\';color:#666666;\"></span></span> \n</h3>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>\n<p align=\"left\">\n	<br />\n</p>\n<p align=\"left\">\n	<b style=\"line-height:1.5;font-size:12px;\"><span style=\"line-height:2;font-family:宋体;font-size:24px;\"><span style=\"line-height:2;font-family:\'Microsoft YaHei\';color:#666666;font-size:24px;\"><img src=\"/uploads/1444445491.png\" width=\"577\" height=\"424\" alt=\"\" /><br />\n</span></span></b> \n</p>\n<p align=\"left\">\n	<b style=\"line-height:1.5;font-size:12px;\"><span style=\"line-height:2;font-family:宋体;font-size:24px;\"><span style=\"line-height:2;font-family:\'Microsoft YaHei\';color:#666666;font-size:24px;\"><br />\n</span></span></b> \n</p>\n<p align=\"left\">\n	<br />\n</p>','4','?tag=48','/uploads/14443769392394.jpg','','/uploads/14443769271038.jpg','')
