INSERT INTO transsion_info VALUES('51','社会责任','社会责任','社会责任','<p style=\"text-align:left;\">\n	<br />\n</p>\n<p style=\"text-align:left;\">\n	<span style=\"line-height:1.5;font-family:Microsoft YaHei;color:#666666;font-size:32px;\">对于社会</span> \n</p>\n<p style=\"text-align:left;\" align=\"left\">\n	<span style=\"font-family:宋体;font-size:9pt;\"><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><span style=\"font-family:Microsoft YaHei;color:#666666;\"><span style=\"font-family:Microsoft YaHei;font-size:16px;\"><strong><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;\">· </span></strong></span></span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">为社会创造更多的商业机会以及工作机会，带动周边产业如银行等的发展</span></span><span></span></span> \n</p>\n<p style=\"text-align:left;\" align=\"left\">\n	<span style=\"font-family:宋体;font-size:9pt;\"><span style=\"font-family:Microsoft YaHei;\"><br />\n</span></span>\n</p>\n<p style=\"text-align:left;\" align=\"left\">\n	<span style=\"font-family:宋体;font-size:9pt;\"><span style=\"font-family:Microsoft YaHei;color:#666666;font-size:32px;\">对于经济</span><span></span></span> \n</p>\n<p style=\"text-align:left;\" align=\"left\">\n	<span style=\"font-family:宋体;font-size:9pt;\"><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><span style=\"font-family:Microsoft YaHei;color:#666666;\"><span style=\"font-family:Microsoft YaHei;font-size:16px;\"><strong><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;\">· </span></strong></span></span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">持续为当地经济做出自己的贡献</span></span><span></span></span> \n</p>\n<p style=\"text-align:left;\" align=\"left\">\n	<span style=\"font-family:宋体;font-size:9pt;\"><span style=\"font-family:Microsoft YaHei;\"><br />\n</span></span>\n</p>\n<p style=\"text-align:left;\" align=\"left\">\n	<span style=\"font-family:宋体;font-size:9pt;\"><span style=\"font-family:Microsoft YaHei;color:#666666;font-size:32px;\">对于人们</span><span></span></span> \n</p>\n<p style=\"text-align:left;\" align=\"left\">\n	<span style=\"font-family:宋体;font-size:9pt;\"><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\"><span style=\"font-family:Microsoft YaHei;color:#666666;\"><span style=\"font-family:Microsoft YaHei;font-size:16px;\"><strong><span style=\"line-height:2;font-family:Microsoft YaHei;color:#000000;\">· </span></strong></span></span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">为人们的生活通讯提供更便利的设备</span></span></span> \n</p>\n<p style=\"text-align:left;\">\n	<br />\n</p>','5','?tag=51','','','/uploads/13993462417192responsibility.jpg','')
INSERT INTO transsion_info VALUES('52','','','','<p>\n	<span style=\"line-height:2;font-family:\'Microsoft YaHei\';font-size:14px;\"></span>&nbsp;\n</p>\n<p>\n	<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';font-size:14px;\">在非洲多个国家如尼日利亚、肯尼亚、喀麦隆、坦桑尼亚、乌干达等建立了多个售后中心，为消费者带去优质的服务。</span> \n</p>\n<p>\n	<span style=\"line-height:28px;font-family:\'Microsoft YaHei\';white-space:normal;font-size:14px;\"></span>&nbsp;\n</p>\n<p>\n	<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';white-space:normal;font-size:14px;\">2012年，传音在埃塞俄比亚成立了工厂以及售后服务中心，为当地带去移动通讯行业的各种技术。</span>\n</p>','5','?tag=52','/uploads/13996235492727.jpg','','/uploads/13996235447617.jpg','<br />')
