INSERT INTO transsion_info_en VALUES('51','Responsibility','Responsibility','Responsibility','<p style=\"text-align:center;\">\n	<span style=\"color:#666666;font-family:Tahoma;font-size:32px;line-height:2;\"> </span> \n</p>\n<p style=\"text-align:left;\">\n	<span style=\"color:#666666;font-family:Tahoma;font-size:12px;line-height:2;\">&nbsp;&nbsp;</span>\n</p>\n<p style=\"text-align:left;\">\n	<span style=\"color:#666666;font-family:Tahoma;font-size:32px;line-height:2;\">For Society</span>\n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-family:Tahoma;font-size:14px;line-height:2;\"><strong style=\"text-align:justify;white-space:normal;font-family:\'Microsoft YaHei\';font-size:16px;line-height:32px;\"><span style=\"line-height:2;\">· </span></strong><span style=\"font-size:16px;\">Help to boost the\ndevelopment of the peripheral industry, logistics and banks, etc.; create lots\nof job opportunities.</span></span> \n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-family:Tahoma;font-size:12px;line-height:2;\"><span style=\"font-size:12px;\">&nbsp;</span></span>\n</p>\n<p style=\"text-align:left;\">\n	<span style=\"color:#666666;font-family:Tahoma;font-size:32px;line-height:2;\">For Economy</span> \n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-family:Tahoma;font-size:14px;line-height:2;\"><strong style=\"text-align:justify;white-space:normal;font-family:\'Microsoft YaHei\';font-size:16px;line-height:32px;\"><span style=\"line-height:2;\">· </span></strong><span style=\"font-size:16px;\">Make our own contribution\nto the development of the local economy continuously.</span></span> \n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-family:Tahoma;font-size:14px;line-height:2;\"><span style=\"font-size:16px;\"><span style=\"font-size:12px;\">&nbsp; &nbsp;&nbsp;</span> </span></span>\n</p>\n<p style=\"text-align:left;\">\n	<span style=\"color:#666666;font-family:Tahoma;font-size:32px;line-height:2;\">For People</span> \n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-family:Tahoma;font-size:14px;line-height:2;\"><strong style=\"text-align:justify;white-space:normal;font-family:\'Microsoft YaHei\';font-size:16px;line-height:32px;\"><span style=\"line-height:2;\">· </span></strong><span style=\"font-size:16px;\">Provide portable,\nfavorable and affordable mobile device to people.</span></span> \n</p>\n<p>\n	<span style=\"font-family:Tahoma;font-size:14px;line-height:2;\"><br />\n</span> \n</p>\n<p>\n	<span style=\"font-family:Tahoma;font-size:14px;line-height:2;\"><br />\n</span> \n</p>\n<p>\n	<span style=\"font-family:Tahoma;font-size:14px;line-height:2;\"><br />\n</span> \n</p>','5','?tag=51','','','/uploads/13993443493465responsibility.jpg','')
INSERT INTO transsion_info_en VALUES('52','','','','<p>\n	<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\"></span>&nbsp;\n</p>\n<p>\n	<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\">TRANSSION HOLDINGS has built a lot of after-sales service centers in many countries in Africa to provide excellent service to consumers.</span> \n</p>\n<p>\n	<br />\n</p>\n<p>\n	<span style=\"line-height:28px;font-family:Tahoma;white-space:normal;font-size:14px;\">In 2012, the company has established a factory and after-sales service centers in Ethiopia, bringing all kinds of technology in mobile communications industry to the country.</span> \n</p>\n<p>\n	<br />\n</p>','5','?tag=52','/uploads/13996234595157.jpg','','/uploads/13996233114885.jpg','<p>\n	<br />\n</p>')
INSERT INTO transsion_info_en VALUES('53','','','','<p>\n	<br />\n</p>\n<p style=\"text-align:justify;\">\n	<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\">\n</p>\n</span> \n<ul>\n	<li>\n		<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\">In April 2012, gave football uniform to the students in high school in Kenya, lighting up their football dream</span> \n	</li>\n	<li>\n		<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\">In June 2012, cleaned every Shoeshine House beside every bus station in Ethiopia for the passengers who are waiting for bus</span> \n	</li>\n	<li>\n		<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\">In August 2012, took part in the activity of exhorting teenagers not to take drugs in Kenya</span> \n	</li>\n	<li>\n		<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\">On October 1, 2012, held an activity of caring for the aged during the International day of old persons in Kenya</span> \n	</li>\n	<li>\n		<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\">On June 8, 2013, TECNO sponsored the charity game themed “the Michael Essien Foundation Game of Hope and Inspiration” which was aimed at raising funds for five African charities yet to be determined.</span> \n	</li>\n	<li>\n		<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\">In August, 2013, TECNO officially unveiled as sponsor for Ghana’s Fastest Human which aims at selecting the most talented people in sports for Ghana.</span> \n	</li>\n	<li>\n		<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\">In January, 2014, TECNO started a campaign to make a better environment for Nigeria computer village by cleaning the streets.</span> \n	</li>\n</ul>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>','5','?tag=53','/uploads/13996150559156.jpg','','/uploads/13996149817040.jpg','')
INSERT INTO transsion_info_en VALUES('54','','','','<p>\n	<span style=\"line-height:2;font-family:Tahoma;color:#666666;font-size:24px;\"><br />\n</span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:Tahoma;color:#666666;font-size:24px;\">Sales Skills Training &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style=\"line-height:48px;font-family:Tahoma;white-space:normal;color:#666666;font-size:24px;\">Market Knowledge Training</span></span> \n</p>\n<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\">Offer sales skill training for the local employees in each office &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style=\"line-height:28px;font-family:Tahoma;white-space:normal;font-size:14px;\">Offer market knowledge training for the local employees in each office</span></span><br />\n<br />\n<span style=\"line-height:2;font-family:Tahoma;color:#666666;font-size:24px;\">App Design Training &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<span style=\"line-height:48px;font-family:Tahoma;white-space:normal;color:#666666;font-size:24px;\">Technical Maintenance Training</span></span><br />\n<p>\n	<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\">Offer app design training for local people, &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span style=\"line-height:28px;font-family:Tahoma;white-space:normal;font-size:14px;\">Offer technical maintenance training for local Carlcare employees in each service center</span></span> \n</p>\n<p>\n	<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\">and hold app design contests in Ethiopia and Kenya</span> \n</p>\n<blockquote style=\"border-bottom:medium none;border-left:medium none;padding-bottom:0px;margin:0px 0px 0px 40px;padding-left:0px;padding-right:0px;border-top:medium none;border-right:medium none;padding-top:0px;\">\n	<p>\n		<br />\n	</p>\n	<p>\n		<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\"><br />\n</span> \n	</p>\n	<p>\n		<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\"><br />\n</span> \n	</p>\n	<p>\n		<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\"><br />\n</span> \n	</p>\n	<p>\n		<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\"><br />\n</span> \n	</p>\n	<p>\n		<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\"><br />\n</span> \n	</p>\n	<p>\n		<span style=\"line-height:2;font-family:Tahoma;font-size:14px;\"><br />\n</span> \n	</p>\n</blockquote>','4','?tag=54','/uploads/14443771845384.jpg','','/uploads/14443771723264.jpg','')
