INSERT INTO transsion_job VALUES('5','海外渠道销售经理','','','<p style=\"text-align:left;\">\n	<span style=\"font-family:\'宋体\';font-size:9pt;font-weight:bold;\"></span> \n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;font-weight:bold;\">岗位职责:</span><span style=\"font-family:\'宋体\';font-size:9pt;\"></span> \n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\">1、协助国家经理进行重点客户的开发、管理</span><span style=\"font-family:\'宋体\';font-size:9pt;\"></span> \n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\">2、负责所在区域终端建设、销售网络布局、渠道开拓、客户管理、订单管理等</span><span style=\"font-family:\'宋体\';font-size:9pt;\"></span> \n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\">3、协助制定并执行国家的市场拓展方案、销售政策及市场推广方案等</span><span style=\"font-family:\'宋体\';font-size:9pt;\"></span> \n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\">4、完成所在区域销售任务</span><span style=\"font-family:\'宋体\';font-size:9pt;\"></span> \n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\">5、本地雇员团队的管理</span><span style=\"font-family:\'宋体\';font-size:9pt;\"></span> \n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-family:\'宋体\';font-size:9pt;font-weight:bold;\"></span> \n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;font-weight:bold;\">任职要求：</span><span style=\"font-family:\'宋体\';font-size:9pt;\"></span> \n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\">1、2年以上海外工作经验，4年以上渠道销售经验</span><span style=\"font-family:\'宋体\';font-size:9pt;\"></span> \n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\">2、实地开发和管理客户的经验，海外市场自主品牌拓展和渠道规划经验</span><span style=\"font-family:\'宋体\';font-size:9pt;\"></span> \n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\">3、本科及以上学历，英语或法语听说读写流利，可作为工作语言</span><span style=\"font-family:\'宋体\';font-size:9pt;\"></span> \n</p>\n<p style=\"text-align:left;\">\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\">4、较强的适应能力与沟通能力，思路清晰</span><span style=\"font-family:\'宋体\';font-size:9pt;\"></span> \n</p>\n<p style=\"text-align:left;\">\n	<br />\n</p>\n<p>\n	<br />\n</p>','0','2014-05-16 15:00:47','13986684494488.php','市场类','social')
INSERT INTO transsion_job VALUES('6','SAP实施顾问','','','<p>\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\"><strong></strong></span> \n</p>\n<p>\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\"><strong>岗位职责：</strong></span><span style=\"font-family:\'宋体\';\"></span> \n</p>\n<p>\n	<span style=\"font-family:\'宋体\';\"><span style=\"font-family:Microsoft YaHei;font-size:14px;\">1</span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">、参与公司</span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">SAP</span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">系统实施项目，负责模块实施工作推进及方案评审，指导本模块成员工作</span></span><span style=\"font-family:\'宋体\';\"></span> \n</p>\n<p>\n	<span style=\"font-family:\'宋体\';\"><span style=\"font-family:Microsoft YaHei;font-size:14px;\">2</span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">、在</span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">SLA</span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">的规范与要求下，负责系统运维工作，受理用户服务请求，积极跟进并解决系统问题，保障系统稳定运行</span></span><span style=\"font-family:\'宋体\';\"></span> \n</p>\n<p>\n	<span style=\"font-family:\'宋体\';\"><span style=\"font-family:Microsoft YaHei;font-size:14px;\">3</span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">、监控系统及流程的运行状况，及时处理问题以规避各种系统风险</span></span><span style=\"font-family:\'宋体\';\"></span> \n</p>\n<p>\n	<span style=\"font-family:\'宋体\';\"><span style=\"font-family:Microsoft YaHei;font-size:14px;\">4</span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">、组织应用系统操作技能及相关流程的培训</span></span><span style=\"font-family:\'宋体\';\"></span> \n</p>\n<p>\n	<span style=\"font-family:\'宋体\';\"><span style=\"font-family:Microsoft YaHei;font-size:14px;\">5</span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">、负责相关模块的业务流程及系统功能优化</span></span><span style=\"font-family:\'宋体\';\"></span> \n</p>\n<p>\n	<span style=\"font-family:\'宋体\';\"><span style=\"font-family:Microsoft YaHei;font-size:14px;\">6</span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">、参与公司其他信息系统建设</span></span> \n</p>\n<p>\n	<span style=\"font-family:\'宋体\';\"><span style=\"font-family:Microsoft YaHei;font-size:14px;\"></span></span><span style=\"font-family:\'宋体\';\"></span> \n</p>\n<p>\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\"><strong>任职要求：</strong></span><span style=\"font-family:\'宋体\';\"></span> \n</p>\n<p>\n	<span style=\"font-family:\'宋体\';\"><span style=\"font-family:Microsoft YaHei;font-size:14px;\">1</span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">、五年以上</span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">SAP</span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">任意模块的项目实施及运维经验：</span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">SD/PP</span></span><span style=\"font-family:\'宋体\';\"></span> \n</p>\n<p>\n	<span style=\"font-family:\'宋体\';\"><span style=\"font-family:Microsoft YaHei;font-size:14px;\">2</span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">、至少两个以上完整项目实施经验，有模块组长经验者优先</span></span><span style=\"font-family:\'宋体\';\"></span> \n</p>\n<p>\n	<span style=\"font-family:\'宋体\';\"><span style=\"font-family:Microsoft YaHei;font-size:14px;\">3</span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">、能主动承担工作压力，具备独立处理问题的能力</span></span><span style=\"font-family:\'宋体\';\"></span> \n</p>\n<p>\n	<span style=\"font-family:\'宋体\';\"><span style=\"font-family:Microsoft YaHei;font-size:14px;\">4</span><span style=\"font-family:Microsoft YaHei;font-size:14px;\">、有较强的沟通理解能力、书面和口头表达能力及管理协调能力</span></span> \n</p>\n<p>\n	<br />\n</p>\n<p>\n	<br />\n</p>','0','2014-05-17 12:09:07','','IT类','social')
